mapper.select:

	select          no,
                    name
	from            users
	where id =      #{id}
	and password =  #{password}


select  id
from    users
where   id=#{id}

mapper.update:

			update users
			set    id = #{id},
       			   password = #{password},
			       name = #{name},
			       gender = #{gender} 
			where  no = #{no}
